import { OrbitControls } from "@react-three/drei";
import { Canvas, useFrame } from "@react-three/fiber";
import { useMemo, useRef } from "react";
import { Color } from "three";

import vertexShader from './VertexShader';
import fragmentShader from './FragmentShader';

// colors it
// const fragmentShader = `...`;
// makes it move
// const vertexShader = `...`;

// console.log(["red", "pink", "blue"].map(c => new Color(c)));

const Fragment = () => {
  // This reference will give us direct access to the mesh
  const mesh = useRef();
  const uniforms = useMemo(
    () => ({
      u_a_color: {
        value: new Color("#D92E2E")
      },
      u_b_color: {
        value: new Color("#2EBAD9")
      },
      u_c_color: {
        value: new Color("#D9C82E")
      },
    }), []
  );

  return (
    <mesh ref={mesh} position={[0, 0, 0]} scale={1.0}>
      <planeGeometry args={[1, 1, 32, 32]} />
      <shaderMaterial vertexColors={true} 
        fragmentShader={fragmentShader}
        vertexShader={vertexShader}
        uniforms={uniforms}
      />
    </mesh>
  );
};

const Background = () => {
  return (
    <Canvas camera={{ position: [0.0, 0.0, 1.0] }}>
      <Fragment />
      <OrbitControls />
    </Canvas>
  );
};


export default Background
