//         vertexShader={vertexShader}
//         uniforms={uniforms}
//         wireframe
//       />
//     </mesh>
//   );
// };


const Gradient = () => {
  // This reference will give us direct access to the mesh
const mesh = useRef();
const mousePosition = useRef({ x: 0, y: 0 });

const updateMousePosition = useCallback((e) => {
  mousePosition.current = { x: e.pageX, y: e.pageY };
}, []);

const uniforms = useMemo(
  () => ({
    u_time: {
      value: 0.0,
    },
    u_mouse: { value: new Vector2(0, 0) },
    u_bg: {
      value: new Color("#A1A3F7"),
    },
    u_colorA: { value: new Color("#9FBAF9") },
    u_colorB: { value: new Color("#FEB3D9") },
  }),
  []
);

useEffect(() => {
  window.addEventListener("mousemove", updateMousePosition, false);

  return () => {
    window.removeEventListener("mousemove", updateMousePosition, false);
  };
}, [updateMousePosition]);

useFrame((state) => {
  const { clock } = state;

  mesh.current.material.uniforms.u_time.value = clock.getElapsedTime();
  mesh.current.material.uniforms.u_mouse.value = new Vector2(
    mousePosition.current.x,
    mousePosition.current.y
  );
});

return (
  <mesh ref={mesh} position={[0, 0, 0]} scale={1.5}>
    <planeGeometry args={[1, 1, 32, 32]} />
    <shaderMaterial
      fragmentShader={fragmentShader}
      vertexShader={vertexShader}
      uniforms={uniforms}
      wireframe={false}
    />
  </mesh>
);
};


const Fragment = () => {
  // This reference will give us direct access to the mesh
  const mesh = useRef();

  // const mousePosition = useRef({ x: 0, y: 0 });

  // const updateMousePosition = useCallback((e) => {
  //   mousePosition.current = { x: e.pageX, y: e.pageY };
  // }, []);

  const uniforms = useMemo(
    () => ({
      u_time: {
        value: 0.0,
      },
      u_mouse: { value: new Vector2(0, 0) },
      u_bg: {
        value: new Color("#D9C82E"),
      },
      u_colorA: { value: new Color("#D92E2E") },
      u_colorB: { value: new Color("#2EBAD9") },
    }), []
  );

  useFrame((state) => {
    const { clock } = state;
    mesh.current.material.uniforms.u_time.value = clock.getElapsedTime();
  });

  return (
    <mesh ref={mesh} position={[0, 0, 0]} rotation={[-Math.PI / 2, 0, 0]} scale={1.5}>
      <planeGeometry args={[1, 1, 32, 32]} />
      <shaderMaterial vertexColors={true} 
        fragmentShader={fragmentShader}
        vertexShader={vertexShader}
        uniforms={uniforms}
      />
    </mesh>
  );
};

const Background = () => {
  return (
    <Canvas camera={{ position: [0.0, 0.0, 1.5] }}>
      <Fragment />
      <OrbitControls />
    </Canvas>
  );
};


export default Background
